function menu_toggle(e){e.classList.toggle("show")}function showNews(){document.getElementById("all-news").style.display="none";getData("GET","json/data.json",function(e,n){e?alert(e.message):newsTemplate.render(n,function(e,n){if(e)throw e;newsWrap.insertAdjacentHTML("beforeend",n)})})}var newsWrap=document.getElementById("news-wrapper")||null,newsTemplate=new Template(['<div class="news-item">','<a href="#">','<img class="news-image" src="{{ image }}" alt="inf">',"</a>",'<a class="item-title" href="#">',"<h4>{{ title }}</h4>","</a>",'<p class="item-description">{{ text }}</p>',"</div>"]);if(newsWrap){var ROOT="json/data.json";getData("GET",ROOT,function(e,n){e?alert(e.message):(newsWrap.innerHTML='<img src="img/preloader.gif" alt="preloader">',setTimeout(function(){newsTemplate.render(n,function(e,n){if(e)throw e;newsWrap.innerHTML=n})},3e3))})}